import { Component } from '@angular/core';

import pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts';

import { File } from '@ionic-native/File/ngx';
import { FileOpener } from '@ionic-native/file-opener/ngx';

import { Platform } from '@ionic/angular';

@Component({
  selector: 'app-tab1',
  templateUrl: 'tab1.page.html',
  styleUrls: ['tab1.page.scss']
})

export class Tab1Page {
  
  todo = {
    date: '',
    name: '',
    mes: '',
    recargo: '',
    monto: ''
  }
  
  logForm() {
    console.log(this.todo)
  }

  pdfObj = null;
 
  constructor(private plt: Platform, private file: File, private fileOpener: FileOpener) {
    this.GetDate();
    //console.log("asd")
  }
 
  today = new Date();
  
  ionViewDidLoad() {
    this.plt.ready().then(() => { 
      this.GetDate();
      console.log("asd")
      });

  }

  GetDate(){
    var dd = this.today.getDate();
    var mm = this.today.getMonth()+1; //January is 0!
    var yyyy = this.today.getFullYear();
    var ddVar:any;
    var mmVar:any;

    if(dd<10) {
        ddVar = '0'+ dd.toString();
    } 

    if(mm<10) {
        mmVar = '0'+mm.toString();
    } 

    //today = dd + '/' +  mm + '/' + yyyy;
    //this.today = yyyy + '-' +  mm + '-' + dd;
    //this.today = new Date(yyyy, mm, dd);
    this.todo.date = yyyy + "-" + mmVar + "-" +ddVar;
    console.log(this.todo.date);

    //document.write(today);
    //document.getElementById("fecha_hora").val = today;
    //alert(document.getElementById('fecha_hora').val);
  }

  createPdf() {
    //console.log(this.today);
    //this.GetDate();
    if (!this.todo.monto){
      this.todo.monto = "0";
    }

    var docDefinition = {
      content: [
        
       /*
        {
        alignment: 'justify',
        columns: [
            [
              {text: 'Lorem ipsum dolor1'},
              {text: 'Lorem ipsum dolor2'}
            ],

            [
              {text: 'Lorem ipsum dolor3'},
              {text: 'Lorem ipsum dolor4'}
            ]
        ]
        },
        */

        //Logo
        {
	        image: 'data:image/jpeg;base64,',
          width: 200,
          alignment: 'center'
        },
        
        { text: '\nComprobante', style: 'header', alignment: 'center'  },

       {
        style: 'tableExample',
        table: {
          widths: ['*','*'],
          body: [
            //['Column 1', 'Column 2', 'Column 3'],
            [ //Columna 1
              {
                stack: [
                  { text: this.todo.date, alignment: 'left', style: 'date'},      //1
                  { text: 'Nombre:', style: 'subheader', alignment: 'left' },     //2
                  { text: 'Mes Abonado', style: 'subheader', alignment: 'left' }, //3
                  { text: 'Recargo:', style: 'subheader', alignment: 'left' },    //4
                  
                ]
              },
              {
                stack: [
                  { text: "    " , alignment: 'left' , style: 'date'},
                  { text: this.todo.name , alignment: 'left' , style: 'subheaderInput'},
                  { text: this.todo.mes, alignment: 'left', style: 'subheaderInput'},
                  { text: this.todo.recargo, alignment: 'left', style: 'subheaderInput'},
                ]     
              },
            ], //Fin columna 1
            [ //Columna 2
              { text: 'Monto:', style: 'monto', alignment: 'left' },          //5
              {text: "$"+ (Math.round(parseFloat(this.todo.monto) * 100) / 100).toFixed(2).toString(), alignment: 'left', style: 'monto'},
            ] //Fin Columna 2
          ] // Fin body
        },
        //layout: 'headerLineOnly'
      },


      ],
      styles: {
        tableExample: {
          margin: [0, 5, 0, 15]
        },
        header: {
          fontSize: 22,
          bold: true,
        },
        date: {
          fontSize: 18,
          bold: true,
        },
        subheader: {
          fontSize: 14,
          bold: true,
          margin: [0, 15, 0, 0]
        },
        monto: {
          fontSize: 16,
          bold: true,
          margin: [0, 15, 0, 0]
        },
        subheaderInput: {
          fontSize: 14,
          margin: [0, 15, 0, 0]
        },
        story: {
          italic: true,
          alignment: 'center',
          width: '50%',
        }
      }
    }
    this.pdfObj = pdfMake.createPdf(docDefinition);
    this.downloadPdf();
  }
 
  downloadPdf() {
    if (this.plt.is('cordova')) {
      this.pdfObj.getBuffer((buffer) => {
        var blob = new Blob([buffer], { type: 'application/pdf' });
 
        // Save the PDF to the data Directory of our App
        this.file.writeFile(this.file.dataDirectory, 'myletter.pdf', blob, { replace: true }).then(fileEntry => {
          // Open the PDf with the correct OS tools
          this.fileOpener.open(this.file.dataDirectory + 'myletter.pdf', 'application/pdf');
        })
      });
    } else {
      // On a browser simply use download!
      this.pdfObj.download();
    }
  }
}

